INSERT INTO `experiment` (`id`, `name`, `request`, `context`) VALUES (1, 'Linux的环境认识与常用命令使用', '1、熟悉Linux常用命令；\r\n2、掌握Linux命令参数。\r\n3、装有Linux操作系统的微型计算机；（包括但不限于Centos，Ubuntu）', 'Linux实验1要求.doc');
INSERT INTO `experiment` (`id`, `name`, `request`, `context`) VALUES (2, 'Linux进程编程技术', '1.进程的基本概念及进程的结构\r\n2.Linux环境下进程的相关函数的应用\r\n3.守护进程的概念、启动和建立\r\n4.进程控制程序的编写', 'Linux实验2要求.doc');
INSERT INTO `experiment` (`id`, `name`, `request`, `context`) VALUES (3, 'Linux内存管理编程技术', '1结合Linux系统调用进一步理解malloc\r\n2 学习内核模块编程技术\r\n3理解并实践内存缺页相关知识', 'Linux实验3要求.doc');
INSERT INTO `experiment` (`id`, `name`, `request`, `context`) VALUES (4, 'Linux中断与异常编程技术', '1了解linux时钟中断的原理\r\n2 学习进程定时器技术', 'Linux实验4要求.doc');
INSERT INTO `experiment` (`id`, `name`, `request`, `context`) VALUES (5, 'Linux系统调用的编程技术', '1了解Linux系统调用\r\n2仔细分析汇编代码调用系统调用的工作过程，特别是参数的传递方式等。\r\n3总结部分需要阐明自己对“系统调用的工作机制”的理解。', 'Linux实验5要求.doc');
INSERT INTO `experiment` (`id`, `name`, `request`, `context`) VALUES (6, 'Linux内核同步编程技术', '1.进程通信中信号概念及信号处理\r\n2.进程间的管道通信编程\r\n3.进程间的内存共享编程', 'Linux实验6要求.doc');
INSERT INTO `experiment` (`id`, `name`, `request`, `context`) VALUES (7, 'Linux文件系统编程技术', '1了解Linux文件系统\r\n2 可以根据proc文件系统获取系统信息', 'Linux实验7要求.doc');
INSERT INTO `experiment` (`id`, `name`, `request`, `context`) VALUES (8, 'Linux设备编程技术', '1了解stty终端模式\r\n2 可以针对终端设备进行开发 ', 'Linux实验8要求.doc');
